const utils = {};

utils.hasJS = function hasJS(JS_CLASS, elementsSelector) {
  const elements = document.querySelectorAll(elementsSelector);

  if (!elements) return;

  elements.forEach((element) => element.classList.add(JS_CLASS));
};

utils.toggleClass = function toggleClass(TOGGLE_CLASS, ctaSelector, event, targetSelector) {
  const cta = document.querySelector(ctaSelector);

  if (!cta) return;

  cta.addEventListener(event, () => {
    document.querySelectorAll(`${ctaSelector}, ${targetSelector}`).forEach((element) => element.classList.toggle(TOGGLE_CLASS));
  });
};

export default utils;
