export const UPDATE_LOAN_AMOUNT = 'update-loan-amount';
export const UPDATE_TERM = 'update-term';

export function updateLoanAmountAction(loanAmount) {
  return {
    type: UPDATE_LOAN_AMOUNT,
    loanAmount,
  };
}

export function updateTermAction(loanTerm) {
  return {
    type: UPDATE_TERM,
    loanTerm,
  };
}
