import React from 'react';
import PropTypes from 'prop-types';

const LoanAmountRangeInput = ({
  maxLoanAmount, minLoanAmount, loanAmount, updateLoanAmount,
}) => (
  <input
    type="range"
    min={ minLoanAmount }
    max= { maxLoanAmount }
    value={ loanAmount }
    onChange={ e => updateLoanAmount(parseInt(e.target.value, 10)) }
  />
);
LoanAmountRangeInput.propTypes = {
  minLoanAmount: PropTypes.number,
  maxLoanAmount: PropTypes.number,
  loanAmount: PropTypes.number,
  updateLoanAmount: PropTypes.func,
};
export default LoanAmountRangeInput;
