import React from 'react';
import PropTypes from 'prop-types';

function LoanBreakdown({
  monthlyPayments,
  interestFee,
  completionFee,
  totalCost,
}) {
  return (
    <div>
      <h2>Loan Breakdown</h2>
      <dl>
        <dt>Fixed monthly payments</dt>
        <dd data-test-monthly-payments>{ monthlyPayments }</dd>
        <dt>Total interest</dt>
        <dd data-test-total-interest>{ interestFee }</dd>
        <dt>Completion fee</dt>
        <dd data-test-completion-fee>{ completionFee }</dd>
        <dt>Total cost of loan</dt>
        <dd data-test-total-cost>{ totalCost }</dd>
      </dl>
    </div>
  );
}

LoanBreakdown.propTypes = {
  monthlyPayments: PropTypes.string,
  interestFee: PropTypes.string,
  completionFee: PropTypes.string,
  totalCost: PropTypes.string,
};
export default LoanBreakdown;
