import React from 'react';
import { shallow } from 'enzyme';
import LoanBreakdown from '../loan-breakdown';

describe('LoanBreakdown component', () => {
  let loanBreakdown;
  beforeEach(() => {
    loanBreakdown = shallow(<LoanBreakdown 
        monthlyPayments={ '£12.00' } 
        interestFee={ '$45,000'} 
        completionFee={ '€123' } 
        totalCost={ '$456' } 
        />);
  });

  describe('when the component is rendered', () => {
    it('should display the formatted monthly payment', () => {
      expect(loanBreakdown.find('[data-test-monthly-payments]').text()).toBe('£12.00'); 
    });
    it('should display the formatted interest fee', () => {
      expect(loanBreakdown.find('[data-test-total-interest]').text()).toBe('$45,000');
    });
    it('should display the formatted completion fee', () => {
      expect(loanBreakdown.find('[data-test-completion-fee]').text()).toBe('€123');
    });
    it('should display the formatted total cost', () => {
      expect(loanBreakdown.find('[data-test-total-cost]').text()).toBe('$456');
    });
  });
});
