import { connect } from 'react-redux';
import { updateLoanAmountAction } from '../actions';
import LoanAmountNumericInput from '../components/loan-amount-numeric-input';

function mapStateToProps({
  loanAmount, locale, minLoanAmount, maxLoanAmount, errorMessage,
}) {
  return {
    loanAmount,
    locale,
    minLoanAmount,
    maxLoanAmount,
    errorMessage,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    updateLoanAmount: (value) => {
      dispatch(updateLoanAmountAction(value));
    },
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(LoanAmountNumericInput);
