import { connect } from 'react-redux';
import { updateLoanAmountAction } from '../actions';
import LoanAmountRangeInput from '../components/loan-amount-range-input';

function mapStateToProps({ loanAmount, minLoanAmount, maxLoanAmount }) {
  return { 
    loanAmount, 
    minLoanAmount, 
    maxLoanAmount,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    updateLoanAmount: (value) => {
      dispatch(updateLoanAmountAction(value));
    },
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(LoanAmountRangeInput);
