import {
  UPDATE_LOAN_AMOUNT,
  UPDATE_TERM,
  updateLoanAmountAction,
  updateTermAction,
} from '../actions';

describe('actions', () => {
  describe('updateLoanAmount()', () => {
    it('should return action with loan amount', () => {
      expect(updateLoanAmountAction(1314)).toEqual({
        type: UPDATE_LOAN_AMOUNT,
        loanAmount: 1314,
      });
    });
  });
  describe('updateTermAction()', () => {
    it('should return action with loan term', () => {
      expect(updateTermAction(12)).toEqual({
        type: UPDATE_TERM,
        loanTerm: 12,
      });
    });
  });
});
