import calculateAmortisedValue from '../calculate-amortisation';

describe('calculateAmortisedValue()', () => {
  it('should calculate value', () => {
    // check with math department that these are correct.
    const expected = {
      monthlyPayments: 6159,
      totalCost: 73905,
      completionFee: 1750,
      interestFee: 2155,
    };

    const loanAmount = 70000;
    const loanTerm = 12;

    expect(calculateAmortisedValue(loanAmount, loanTerm)).toEqual(expected);
  });
});
