import getURL from '../global/get-url';

describe('getURL', () => {
  beforeAll(() => {
    getURL();
  });

  it('should be a function', () => {
    expect(typeof getURL).toBe('function');
  });

  it('should return an instance of a URL object', () => {
    expect(window.getURL().constructor.name).toBe('URL');
  });

  it('should create a URL object from window.location.href', () => {
    expect(window.getURL()).toEqual(new URL(window.location.href));
  });
});
