import quote from '../components/quote';

describe('Quote component in styleguide', () => {
  beforeEach(() => {
    jasmine.getFixtures().fixturesPath = 'base/src/m2dm/documentation/js/tests/fixtures';
  });

  describe('select box', () => {
    beforeEach(() => {
      jasmine.getFixtures().load('localeSelect.html');
      quote();
    });

    it('switches the lang attribute on the html tag, when select changes', () => {
      const select = document.querySelector('.html-lang-input');
      const localeCode = 'de';

      select.value = localeCode;
      select.dispatchEvent(new Event('change'));

      expect(document.documentElement.lang).toBe(localeCode);
    });
  });
});
