import hasJs from '../index';

describe('hasJs', () => {
  let elemBody;
  const CLASS_JS = 'has-js';

  beforeEach(() => {
    elemBody = document.body;
  });

  describe('Before starting the script', () => {
    it('No class is present in the body', () => {
      expect(elemBody.classList).not.toContain(CLASS_JS);
    });
  });

  describe('After initiating the script', () => {
    beforeEach(() => {
      hasJs();
    });

    it('The class is present', () => {
      expect(elemBody.classList).toContain(CLASS_JS);
    });
  });
});
